import React, { useState, useEffect } from 'react'
import axios from 'axios'
import NewsItem from '../newsitem'
import './newslist.css'


const NewsList = () => {
    const [articles, setArticles] = useState([])
    const [artic, setArtic] = useState([])

    useEffect(() => {
        const getArticles = async () => {
            const response = await axios.get(`https://newsapi.org/v2/everything?q=politics&apiKey=58b560b1b32f4181a5921914afc27b0b`)
            setArticles(response.data.articles)
            console.log(response)
        }
        const getArtic = async () => {
            const response = await axios.get(`https://newsapi.org/v2/everything?q=sports&apiKey=58b560b1b32f4181a5921914afc27b0b`)
            setArtic(response.data.artic)
            console.log(response)
        }

        getArticles()
    }, [])
    return (
        <div className='politicscontainer'>
            {articles.map(article => {
                return(
                
                    <NewsItem 
                        title={article.title}
                        description={article.description}
                        url={article.url}
                        urlToImage={article.urlToImage} 
                        author={article.author}

                    />
                    
                  
                )
            })}
             {artic.map(artic => {
                return(
                
                    <NewsItem 
                        title={artic.title}
                        description={artic.description}
                        url={artic.url}
                        urlToImage={artic.urlToImage} 
                        author={artic.author}

                    />
                    
                  
                )
            })}
        </div>
    )
}

export default NewsList
