import axios from 'axios'
import "./hero.css"
import Card from "./cards.js"
import '../../styles/utils.css'


const Hero = () => {
    const [articles, setArticles] = useState([])

    useEffect(() => {
        const getArticles = async () => {
            const respond = await axios.get(`https://newsapi.org/v2/everything?q=sports&apiKey=58b560b1b32f4181a5921914afc27b0b`)
            setArticles(respond.data.articles)
            console.log(respond)
        }

        getArticles()
    }, [])

  return (
    <>
      <section className='hero maxwidth-1'>
        <div className='container '>
          {articles.map((artic) => {
            return (
              <>
                return(
                    <Card
                        title={artic.title}
                        description={artic.description}
                        url={artic.url}
                        urlToImage={artic.urlToImage} 
                        author={artic.author}

                    />
                    
                )
              </>
            )
          },97,97)}
          
        </div>
      </section>
    </>
  )
}

export default Hero
